mapper.insert:
INSERT INTO board
   SET title = #{title}
     , contents = #{contents}
     , user_id = #{userId}

mapper.select:
SELECT b.id, b.title, b.created_at, u.nm FROM board b INNER JOIN user u ON u.id = b.user_id
WHERE b.title LIKE '%${searchText}%' OR b.contents LIKE '%${searchText}%'
ORDER BY b.id DESC LIMIT #{startIdx}, #{size}
